import React, { useState, useEffect } from "react";
import axios from "axios";

function CurrentProducts() {
  const [products, setProducts] = useState([]);
  const [error, setError] = useState(null);

  const fetchProducts = async () => {
    try {
      // Fetch all products
      const response = await axios.get("http://localhost:5000/api/products");
      const allProducts = response.data;

      // Filter products based on a specific criterion
      // For example, assuming you want to display products with a special flag or category
      const currentProducts = allProducts.filter(product => product.category === "current");

      setProducts(currentProducts);
    } catch (error) {
      console.error("Error fetching products:", error);
      setError("Failed to fetch products. Please try again later.");
    }
  };

  useEffect(() => {
    fetchProducts();
  }, []);

  return (
    <div>
      {error && <p className="alert alert-danger">{error}</p>}
      <h2 className="text-center">Current Products</h2>
      <ul className="list-group mt-4">
        {products.map((product) => (
          <li key={product._id} className="list-group-item list-group-item-success d-flex justify-content-between align-items-center">
            <div>{product.name} - ${product.price}</div>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default CurrentProducts;
